import React from "react";
import {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker
} from "react-google-maps";

const Map = withScriptjs(
  withGoogleMap(props => (
    <GoogleMap
      defaultZoom={8}
      defaultCenter={{ lat: props.markers.lat, lng: props.markers.lng }}
    >
      {props.markers.lat}
      {props.isMarkerShown && (
        <Marker position={{ lat: props.markers.lat, lng: props.markers.lng }} />
      )}
    </GoogleMap>
  ))
);

export default Map;
